import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./AddEvent.css";

const AddEvent = () => {
  const navigate = useNavigate();
  const [eventData, setEventData] = useState({
    eventType: "",
    eventName: "",
    contactNumber: "",
    email: "",
    date: "",
    guestCount: "",
    guestDetails: "",
    specialNotes: "",
  });

  const handleChange = (e) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      // Attempt to post event data to the server
      const response = await axios.post("http://localhost:5000/events", eventData);
      
      // Safely extract the event ID
      const eventId = response.data?._id || response.data?.id;
      
      if (eventId) {
        // Navigate to event summary page with the newly created event's ID
        navigate(`/event-summary/${eventId}`);
      } else {
        // Handle case where no ID is returned
        console.error("No event ID received from server");
        alert("Event submitted, but unable to retrieve event details.");
      }
    } catch (error) {
      // Comprehensive error handling
      console.error("Submission Error:", error);
      
      // Check if there's a specific error response from the server
      if (error.response) {
        // Server responded with an error status
        alert(`Submission failed: ${error.response.data.message || 'Unknown error'}`);
      } else if (error.request) {
        // Request was made but no response received
        alert("No response from server. Please check your network connection.");
      } else {
        // Something happened in setting up the request
        alert("Error in submission process. Please try again.");
      }
    }
  };

  return (
    <div className="add-event">
      <h1>Add New Event</h1>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          name="eventType" 
          placeholder="Event Type" 
          onChange={handleChange} 
          required 
        />
        <input 
          type="text" 
          name="eventName" 
          placeholder="Event Name" 
          onChange={handleChange} 
          required 
        />
        <input 
          type="text" 
          name="contactNumber" 
          placeholder="Contact Number" 
          onChange={handleChange} 
          required 
        />
        <input 
          type="email" 
          name="email" 
          placeholder="Email" 
          onChange={handleChange} 
          required 
        />
        <input 
          type="date" 
          name="date" 
          onChange={handleChange} 
          required 
        />
        <input 
          type="number" 
          name="guestCount" 
          placeholder="Guest Count" 
          onChange={handleChange} 
          required 
        />
        <textarea 
          name="guestDetails" 
          placeholder="Guest Details" 
          onChange={handleChange}
        ></textarea>
        <textarea 
          name="specialNotes" 
          placeholder="Special Notes" 
          onChange={handleChange}
        ></textarea>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AddEvent;
