import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './TaskList.css'; // Create this CSS file

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Fetch all tasks
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tasks');
        setTasks(response.data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };
    fetchTasks();
  }, []);

  // Delete task
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/tasks/${id}`);
      setTasks(tasks.filter(task => task._id !== id));
      alert('Task deleted successfully');
    } catch (err) {
      alert('Failed to delete task');
    }
  };

  // Navigate to update page
  const handleUpdate = (id) => {
    navigate(`/update-task/${id}`);
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="task-list-container">
      <h2>Task List</h2>
      <button 
        className="create-btn"
        onClick={() => navigate('/create-task')}
      >
        Create New Task
      </button>
      
      <div className="task-grid">
        {tasks.map((task) => (
          <div key={task._id} className="task-card">
            <div className="task-header">
              <h3>{task.taskName}</h3>
              <span className={`status-badge ${task.status.replace(' ', '-')}`}>
                {task.status}
              </span>
            </div>
            
            <div className="task-details">
              <p><strong>ID:</strong> {task.taskID}</p>
              <p><strong>Date:</strong> {new Date(task.taskDate).toLocaleDateString()}</p>
              <p><strong>Process:</strong> {task.process}</p>
              <p><strong>Assigned:</strong> {task.person}</p>
              <p><strong>Description:</strong> {task.description}</p>
            </div>
            
            <div className="task-actions">
              <button 
                className="update-btn"
                onClick={() => handleUpdate(task._id)}
              >
                Update
              </button>
              <button 
                className="delete-btn"
                onClick={() => handleDelete(task._id)}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TaskList;
