import React, { useState } from "react";

const ReportActions = () => {
  const [loading, setLoading] = useState({ pdf: false, email: false });
  const [error, setError] = useState(null);

  const handleGeneratePDF = async () => {
    setLoading({ ...loading, pdf: true });
    setError(null);
    try {
      const response = await fetch("http://localhost:5000/api/generate-pdf");
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Error generating PDF");
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `EventReport_${new Date().toISOString().split('T')[0]}.pdf`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error("Error generating PDF:", error);
      setError(error.message);
    } finally {
      setLoading({ ...loading, pdf: false });
    }
  };

  const handleSendEmail = async () => {
    setLoading({ ...loading, email: true });
    setError(null);
    try {
      const response = await fetch("http://localhost:5000/api/send-email", {
        method: 'POST'
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Error sending email');
      }

      alert(data.message);
    } catch (error) {
      console.error("Error sending email:", error);
      setError(error.message);
    } finally {
      setLoading({ ...loading, email: false });
    }
  };

  return (
    <div className="report-actions">
      {error && <div style={{color: 'red', marginBottom: '10px'}}>{error}</div>}
      <button 
        onClick={handleGeneratePDF} 
        disabled={loading.pdf}
      >
        {loading.pdf ? "Generating..." : "Generate Report"}
      </button>
      <button 
        onClick={handleSendEmail} 
        disabled={loading.email}
      >
        {loading.email ? "Sending..." : "Send Email"}
      </button>
    </div>
  );
};

export default ReportActions;
