import React, { useState, useEffect } from 'react';
import './HomePage.css';

function HomePage() {
  // Set initial index to the first image
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const images = [
    '/images/img6.jpg', // Replace with your image file names
    '/images/img7.jpg',
    '/images/img8.jpg',
    '/images/img9.jpg',
    '/images/img10.jpg',
  ];

  // Update the image every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);

    return () => clearInterval(interval); // Clear interval when the component unmounts
  }, []);

  return (
    <div className="homePage">
      <nav className="navbar">
        <ul className="nav-links">
          <li><a href="event">Event</a></li>
          <li><a href="#venue">Venue</a></li>
          <li><a href="#staff">Staff</a></li>
          <li><a href="#chat">Chat Us</a></li>
          <li><a href="about">About Us</a></li>
        </ul>
      </nav>

      <div className="hero-section">
       <h1>You Keep Dreaming... We'll Keep Working...</h1>
       <p>Make it Alway</p>
      </div>

      <div className="image-carousel">
        <img src={images[currentImageIndex]} alt="EventEase" className="carousel-image" />
      </div>
    </div>
  );
}

export default HomePage;
