import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './CreateTaskForm.css'; // Reuse your create form styles

const UpdateTaskForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    taskName: '',
    taskID: '',
    taskDate: '',
    description: '',
    process: '',
    person: 'Miss.Ruwindya',
    status: 'Pending'
  });

  // Fetch task data to populate form
  useEffect(() => {
    const fetchTask = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/tasks/${id}`);
        setFormData(response.data);
      } catch (error) {
        console.error('Error fetching task:', error);
        alert('Failed to load task data');
        navigate('/all-tasks');
      }
    };
    fetchTask();
  }, [id, navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/tasks/${id}`, formData);
      alert('Task updated successfully!');
      navigate('/all-tasks');
    } catch (error) {
      console.error('Error updating task:', error);
      alert(`Failed to update task: ${error.response?.data?.message || error.message}`);
    }
  };

  return (
    <div className="task-form-container">
      <h2>Update Task</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Task Name:</label>
          <input
            type="text"
            name="taskName"
            value={formData.taskName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Task ID:</label>
          <input
            type="text"
            name="taskID"
            value={formData.taskID}
            onChange={handleChange}
            required
            disabled // Typically IDs shouldn't be editable
          />
        </div>

        <div className="form-group">
          <label>Date:</label>
          <input
            type="date"
            name="taskDate"
            value={formData.taskDate ? formData.taskDate.split('T')[0] : ''}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Description:</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Process:</label>
          <input
            type="text"
            name="process"
            value={formData.process}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Assigned Person:</label>
          <select
            name="person"
            value={formData.person}
            onChange={handleChange}
            required
          >
            <option value="Miss.Ruwindya">Miss.Ruwindya</option>
            <option value="Miss.Rashini">Miss.Rashini</option>
            <option value="Mr.THarusha">Mr.THarusha</option>
          </select>
        </div>

        <div className="form-group">
          <label>Status:</label>
          <select
            name="status"
            value={formData.status}
            onChange={handleChange}
            required
          >
            <option value="Pending">Pending</option>
            <option value="In Progress">In Progress</option>
            <option value="Completed">Completed</option>
          </select>
        </div>

        <div className="form-actions">
          <button type="submit" className="submit-btn">Update Task</button>
          <button 
            type="button" 
            className="cancel-btn"
            onClick={() => navigate('/all-tasks')}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default UpdateTaskForm;
