import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./AddEvent.css";

const AddEvent = () => {
  const navigate = useNavigate();
  // 1. ADDED: Form validation state
  const [errors, setErrors] = useState({});

  const [eventData, setEventData] = useState({
    eventType: "",
    eventName: "",
    contactNumber: "",
    email: "",
    date: "",
    guestCount: "",
    guestDetails: "",
    specialNotes: "",
  });

  // 2. ENHANCED: More robust input validation
  const validateForm = () => {
    const newErrors = {};

    // Email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(eventData.email)) {
      newErrors.email = "Please enter a valid email address";
    }

    // Contact number validation (assuming 10 digit number)
    const phoneRegex = /^\d{10}$/;
    if (!phoneRegex.test(eventData.contactNumber)) {
      newErrors.contactNumber = "Contact number must be 10 digits";
    }

    // Date validation (ensure date is not in the past)
    const selectedDate = new Date(eventData.date);
    const today = new Date();
    if (selectedDate < today) {
      newErrors.date = "Event date cannot be in the past";
    }

    // Guest count validation
    if (eventData.guestCount <= 0) {
      newErrors.guestCount = "Guest count must be greater than zero";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEventData(prevData => ({
      ...prevData, 
      [name]: value
    }));

    // 3. ADDED: Clear specific error when user starts typing
    if (errors[name]) {
      setErrors(prevErrors => ({
        ...prevErrors,
        [name]: undefined
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // 4. ADDED: Validate form before submission
    if (!validateForm()) {
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/events", eventData);
      
      const mongoObjectId = response.data._id;
  
      if (mongoObjectId) {
        console.log("Event added successfully with MongoDB ObjectId:", mongoObjectId);
        navigate(`/event-summary/${mongoObjectId}`);
      } else {
        console.error("Error: MongoDB ObjectId is missing in response");
        alert("Failed to retrieve event ID from MongoDB.");
      }
    } catch (error) {
      console.error("Error adding event:", error.response ? error.response.data : error.message);
      alert("Failed to submit the event. Please check the console for errors.");
    }
  };

  return (
    <div className="add-event">
      <h1>Add New Event</h1>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          name="eventType" 
          placeholder="Event Type" 
          onChange={handleChange} 
          required 
        />
        <input 
          type="text" 
          name="eventName" 
          placeholder="Event Name" 
          onChange={handleChange} 
          required 
        />
        <input 
          type="text" 
          name="contactNumber" 
          placeholder="Contact Number" 
          onChange={handleChange} 
          required 
          // 5. ADDED: Error display for contact number
          className={errors.contactNumber ? 'error-input' : ''}
        />
        {errors.contactNumber && <span className="error-message">{errors.contactNumber}</span>}
        
        <input 
          type="email" 
          name="email" 
          placeholder="Email" 
          onChange={handleChange} 
          required 
          // 6. ADDED: Error display for email
          className={errors.email ? 'error-input' : ''}
        />
        {errors.email && <span className="error-message">{errors.email}</span>}
        
        <input 
          type="date" 
          name="date" 
          onChange={handleChange} 
          required 
          // 7. ADDED: Error display for date
          className={errors.date ? 'error-input' : ''}
        />
        {errors.date && <span className="error-message">{errors.date}</span>}
        
        <input 
          type="number" 
          name="guestCount" 
          placeholder="Guest Count" 
          onChange={handleChange} 
          required 
          // 8. ADDED: Error display for guest count
          className={errors.guestCount ? 'error-input' : ''}
        />
        {errors.guestCount && <span className="error-message">{errors.guestCount}</span>}
        
        <textarea 
          name="guestDetails" 
          placeholder="Guest Details" 
          onChange={handleChange}
        ></textarea>
        
        <textarea 
          name="specialNotes" 
          placeholder="Special Notes" 
          onChange={handleChange}
        ></textarea>
        
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AddEvent;
