import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './UpdateTaskForm.css';

const UpdateTaskForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    taskName: '',
    taskID: '',
    taskDate: '',
    description: '',
    process: '',
    person: '',
    status: 'Pending'
  });
  const [loading, setLoading] = useState(true);

  // Fetch task data from API
  useEffect(() => {
    const fetchTask = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/tasks/${id}`);
        const taskData = response.data;
        
        setFormData({
          taskName: taskData.taskName || '',
          taskID: taskData.taskID || '',
          taskDate: taskData.taskDate ? formatDate(taskData.taskDate) : '',
          description: taskData.description || '',
          process: taskData.process || '',
          person: taskData.person || 'Miss.Ruwindya',
          status: taskData.status || 'Pending'
        });
        setLoading(false);
      } catch (error) {
        console.error('Error fetching task:', error);
        alert('Failed to load task data');
        navigate('/all-tasks');
      }
    };
    fetchTask();
  }, [id, navigate]);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/tasks/${id}`, formData);
      alert('Task updated successfully!');
      navigate('/all-tasks');
    } catch (error) {
      console.error('Error updating task:', error);
      alert(`Failed to update task: ${error.response?.data?.message || error.message}`);
    }
  };

  if (loading) return <div className="loading">Loading task data...</div>;

  return (
    <div id="window">
      <div id="window-content">
        <h2>Update Task</h2>
        <form onSubmit={handleSubmit} className="update-form">
          
          <div className="lebel-div">
            <label className="window-lebel">Task Name:</label>
            <input
              className="input-fields"
              type="text"
              name="taskName"
              value={formData.taskName}
              onChange={handleChange}
              required
            />
          </div>

          <div className="lebel-div">
            <label className="window-lebel">Task ID:</label>
            <input
              className="input-fields"
              type="text"
              name="taskID"
              value={formData.taskID}
              onChange={handleChange}
              required
              disabled
            />
          </div>

          <div className="lebel-div">
            <label className="window-lebel">Date:</label>
            <input
              className="input-fields"
              type="date"
              name="taskDate"
              value={formData.taskDate}
              onChange={handleChange}
              required
            />
          </div>

          <div className="lebel-div">
            <label className="window-lebel">Description:</label>
            <textarea
              className="input-fields"
              name="description"
              value={formData.description}
              onChange={handleChange}
              required
              rows="4"
            />
          </div>

          <div className="lebel-div">
            <label className="window-lebel">Process:</label>
            <input
              className="input-fields"
              type="text"
              name="process"
              value={formData.process}
              onChange={handleChange}
              required
            />
          </div>

          <div className="lebel-div">
            <label className="window-lebel">Assigned Person:</label>
            <select
              className="input-fields"
              name="person"
              value={formData.person}
              onChange={handleChange}
              required
            >
              <option value="Miss.Ruwindya">Miss.Ruwindya</option>
              <option value="Miss.Rashini">Miss.Rashini</option>
              <option value="Mr.THarusha">Mr.THarusha</option>
            </select>
          </div>

          <div className="lebel-div">
            <label className="window-lebel">Status:</label>
            <select
              className="input-fields"
              name="status"
              value={formData.status}
              onChange={handleChange}
              required
            >
              <option value="Pending">Pending</option>
              <option value="In Progress">In Progress</option>
              <option value="Completed">Completed</option>
            </select>
          </div>

          <div className="button-div">
            <button className="window-save-button" type="submit">
              Save
            </button>
            <button 
              className="window-cancel-button" 
              type="button" 
              onClick={() => navigate('/all-tasks')}
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default UpdateTaskForm;
