import React, { useState, useEffect } from 'react';
import axios from "axios";
import "./TaskDashboard.css";

const TaskDashboard = () => {
    const [tasks, setTasks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchTasks = async () => {
            try {
                const response = await axios.get("http://localhost:5000/task");
                setTasks(response.data.Task || []);
                setLoading(false);
            } catch (err) {
                setError("Failed to fetch tasks");
                setLoading(false);
                console.error("Error fetching tasks:", err);
            }
        };

        fetchTasks();
    }, []);

    // Count functions
    const countTotal = () => tasks.length;
    const countComplete = () => tasks.filter(task => task.status?.toLowerCase() === 'complete').length;
    const countInProgress = () => tasks.filter(task => task.status?.toLowerCase() === 'inprogress').length;
    const countRejected = () => tasks.filter(task => task.status?.toLowerCase() === 'reject').length;
    const countQualityA = () => tasks.filter(task => task.quality?.toLowerCase() === 'a').length;
    const countQualityB = () => tasks.filter(task => task.quality?.toLowerCase() === 'b').length;
    const countQualityC = () => tasks.filter(task => task.quality?.toLowerCase() === 'c').length;

    if (loading) return <div className="loading">Loading tasks...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div className="task-dashboard-container">
            <header className="dashboard-header">
                <h1>Task Dashboard</h1>
                <div className="action-buttons">
                    <button className="btn-primary">Create New Task</button>
                    <button className="btn-secondary">View All Tasks</button>
                </div>
            </header>

            <div className="stats-container">
                <div className="stats-row">
                    <StatCard 
                        title="Total Tasks" 
                        value={countTotal()} 
                        icon="ðŸ“‹"
                        color="#4e73df"
                    />
                    <StatCard 
                        title="Completed" 
                        value={countComplete()} 
                        icon="âœ…"
                        color="#1cc88a"
                    />
                    <StatCard 
                        title="In Progress" 
                        value={countInProgress()} 
                        icon="ðŸ”„"
                        color="#f6c23e"
                    />
                    <StatCard 
                        title="Rejected" 
                        value={countRejected()} 
                        icon="âŒ"
                        color="#e74a3b"
                    />
                </div>

                <div className="stats-row">
                    <StatCard 
                        title="Quality A" 
                        value={countQualityA()} 
                        icon="â­"
                        color="#36b9cc"
                    />
                    <StatCard 
                        title="Quality B" 
                        value={countQualityB()} 
                        icon="â­â­"
                        color="#5a5c69"
                    />
                    <StatCard 
                        title="Quality C" 
                        value={countQualityC()} 
                        icon="â­â­â­"
                        color="#858796"
                    />
                </div>
            </div>
        </div>
    );
};

// StatCard component for better UI
const StatCard = ({ title, value, icon, color }) => (
    <div className="stat-card" style={{ borderTop: `4px solid ${color}` }}>
        <div className="stat-content">
            <span className="stat-icon">{icon}</span>
            <div>
                <h3>{title}</h3>
                <p className="stat-value">{value}</p>
            </div>
        </div>
    </div>
);

export default TaskDashboard;
