import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './CreateTaskForm.css';

const UpdateTaskForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    taskName: '',
    taskID: '',
    taskDate: '',
    description: '',
    process: '',
    person: '',
    status: ''
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTask = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/tasks/${id}`);
        const taskData = response.data;
        
        console.log(taskData.task.taskName);
        
        setFormData({
          taskName: taskData.task.taskName || '',
          taskID: taskData.task.taskID || '',
          taskDate: taskData.task.taskDate ? taskData.task.taskDate.split('T')[0] : '',
          description: taskData.task.description || '',
          process: taskData.task.process || '',
          person: taskData.task.person || '',
          status: taskData.task.status || ''
        });
        setLoading(false);
      } catch (error) {
        console.error('Error fetching task:', error);
        alert('Failed to load task data');
        navigate('/all-tasks');
      }
    };
    fetchTask();
  }, [id, navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  //Update data function
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/tasks/${id}`, formData);
      alert('Task updated successfully!');
      navigate('/all-tasks');
    } catch (error) {
      console.error('Error updating task:', error);
      alert(`Failed to update task: ${error.response?.data?.message || error.message}`);
    }
  };

  // Delete task function
  const handleDelete = async () => {
    try {
      await axios.delete(`http://localhost:5000/api/tasks/${id}`);
      alert('Task deleted successfully!');
      navigate('/all-tasks');
    } catch (error) {
      console.error('Error deleting task:', error);
      alert(`Delete failed: ${error.response?.data?.message || error.message}`);
    } finally {
      setShowDeleteConfirm(false);
    }
  };

  if (loading) return <div className="loading">Loading task data...</div>;

  return (
    <div className="task-form-container">
      <h2>Update Task</h2>
      <form onSubmit={handleSubmit}>
        {/* Task Name Field */}
        <div className="form-group">
          <label>Task Name:</label>
          <input
            type="text"
            name="taskName"
            value={formData.taskName}
            onChange={handleChange}
            required
          />
        </div>

        {/* Task ID Field */}
        <div className="form-group">
          <label>Task ID:</label>
          <input
            type="text"
            name="taskID"
            value={formData.taskID}
            onChange={handleChange}
            required
            disabled
          />
        </div>

        {/* Date Field */}
        <div className="form-group">
          <label>Date:</label>
          <input
            type="date"
            name="taskDate"
            value={formData.taskDate || ''}
            onChange={handleChange}
            required
          />
        </div>

        {/* Description Field */}
        <div className="form-group">
          <label>Description:</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
            rows="5"
          />
        </div>

        {/* Process Field */}
        <div className="form-group">
          <label>Process:</label>
          <input
            type="text"
            name="process"
            value={formData.process}
            onChange={handleChange}
            required
          />
        </div>

        {/* Assigned Person Field */}
        <div className="form-group">
          <label>Assigned Person:</label>
          <select
            name="person"
            value={formData.person}
            onChange={handleChange}
            required
          >
            <option value="Miss.Ruwindya">Miss.Ruwindya</option>
            <option value="Miss.Rashini">Miss.Rashini</option>
            <option value="Mr.THarusha">Mr.THarusha</option>
          </select>
        </div>

        {/* Status Field */}
        <div className="form-group">
          <label>Status:</label>
          <select
            name="status"
            value={formData.status}
            onChange={handleChange}
            required
          >
            <option value="Pending">Pending</option>
            <option value="In Progress">In Progress</option>
            <option value="Completed">Completed</option>
          </select>
        </div>

        {/* Form Actions */}
        <div className="form-actions">
          <button type="submit" className="submit-btn">
            Update Task
          </button>
          <button 
            type="button" 
            className="cancel-btn"
            onClick={() => navigate('/all-tasks')}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default UpdateTaskForm;
