import React, { useState } from 'react';
import axios from 'axios';


const ReportActions = ({ reportId }) => {
  const [message, setMessage] = useState({ text: '', type: '' });
  const [loading, setLoading] = useState(false);

  const handleGeneratePDF = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/api/reports/generate-pdf', {
        responseType: 'blob'
      });
      
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'EventReport.pdf');
      document.body.appendChild(link);
      link.click();
      link.parentNode.removeChild(link);
      
      setMessage({ text: 'PDF generated successfully!', type: 'success' });
    } catch (error) {
      setMessage({ 
        text: error.response?.data?.error || 'Error generating PDF', 
        type: 'error' 
      });
    } finally {
      setLoading(false);
    }
  };

  const handleSendEmail = async () => {
    setLoading(true);
    try {
      await axios.post('http://localhost:5000/api/reports/send-email');
      setMessage({ text: 'Email sent successfully!', type: 'success' });
    } catch (error) {
      setMessage({ 
        text: error.response?.data?.error || 'Error sending email', 
        type: 'error' 
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="actions-container">
      <h2>Report Actions</h2>
      {message.text && (
        <div className={`message ${message.type}`}>{message.text}</div>
      )}
      
      <div className="action-buttons">
        <button 
          onClick={handleGeneratePDF} 
          disabled={loading || !reportId}
          className="action-btn"
        >
          {loading ? 'Processing...' : 'Generate PDF'}
        </button>
        
        <button 
          onClick={handleSendEmail} 
          disabled={loading || !reportId}
          className="action-btn"
        >
          {loading ? 'Processing...' : 'Send Email'}
        </button>
      </div>
      
      {!reportId && (
        <div className="warning-message">
          Please create and save a report first before performing actions.
        </div>
      )}
    </div>
  );
};

export default ReportActions;
