import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './CreateTaskForm.css'; // Make sure this path is correct

const UpdateTaskForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    taskName: '',
    taskID: '',
    taskDate: '',
    description: '',
    process: '',
    person: 'Miss.Ruwindya',
    status: 'Pending'
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch task data to populate form
  useEffect(() => {
    const fetchTask = async () => {
      try {
        console.log(`Fetching task with ID: ${id}`); // Debug log
        const response = await axios.get(`http://localhost:5000/api/tasks/${id}`);
        console.log('Response data:', response.data); // Debug log
        
        // Ensure date is properly formatted for the date input
        const taskData = response.data;
        if (taskData.taskDate) {
          taskData.taskDate = taskData.taskDate.split('T')[0];
        }
        
        setFormData(taskData);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching task:', error);
        setError(error.message);
        setLoading(false);
        alert('Failed to load task data');
      }
    };
    
    fetchTask();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log('Submitting form data:', formData); // Debug log
      const response = await axios.put(`http://localhost:5000/api/tasks/${id}`, formData);
      console.log('Update response:', response.data); // Debug log
      alert('Task updated successfully!');
      navigate('/all-tasks');
    } catch (error) {
      console.error('Error updating task:', error.response || error);
      alert(`Failed to update task: ${error.response?.data?.message || error.message}`);
    }
  };

  if (loading) return <div className="loading">Loading task data...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="task-form-container">
      <h2>Update Task</h2>
      <form onSubmit={handleSubmit}>
      <div className="form-group">
          <label>Task Name:</label>
          <input
            type="text"
            name="taskName"
            value={formData.taskName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Task ID:</label>
          <input
            type="text"
            name="taskID"
            value={formData.taskID}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Date:</label>
          <input
            type="date"
            name="taskDate"
            value={formData.taskDate}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Description:</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Process:</label>
          <input
            type="text"
            name="process"
            value={formData.process}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Assigned Person:</label>
          <select
            name="person"
            value={formData.person}
            onChange={handleChange}
            required
          >
            <option value="Miss.Ruwindya">Miss.Ruwindya</option>
            <option value="Miss.Rashini">Miss.Rashini</option>
            <option value="Mr.THarusha">Mr.THarusha</option>
          </select>
        </div>

        <div className="form-group">
          <label>Status:</label>
          <select
            name="status"
            value={formData.status}
            onChange={handleChange}
            required
          >
            <option value="Pending">Pending</option>
            <option value="In Progress">In Progress</option>
            <option value="Completed">Completed</option>
          </select>
        </div>

        <div className="form-actions">
          <button type="submit" className="submit-btn">Create Task</button>
          <button 
            type="button" 
            className="cancel-btn"
            onClick={() => navigate('/all-tasks')}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default UpdateTaskForm;
