import React, { useState } from "react";

const ReportForm = ({ onFormSubmit }) => {
  const [formData, setFormData] = useState({
    eventName: "",
    hostName: "",
    contactNo: "",
    email: "",
    date: "",
    invitedGuests: "",
    attendedGuests: "",
    totalCost: "",
    advancedAmount: "",
    pendingAmount: ""
  });
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    // Convert numeric fields to numbers
    const processedValue = ['invitedGuests', 'attendedGuests', 'totalCost', 'advancedAmount', 'pendingAmount'].includes(name)
      ? Number(value)
      : value;
    
    setFormData({ ...formData, [name]: processedValue });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    try {
      const response = await fetch("http://localhost:5000/api/save-report", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Error submitting report');
      }

      alert(data.message);
      onFormSubmit && onFormSubmit();
      // Reset form after successful submission
      setFormData({
        eventName: "", hostName: "", contactNo: "", email: "", date: "",
        invitedGuests: "", attendedGuests: "", totalCost: "",
        advancedAmount: "", pendingAmount: ""
      });
    } catch (error) {
      console.error("Error submitting report:", error);
      setError(error.message);
    }
  };

  return (
    <form className="event-form" onSubmit={handleSubmit}>
      {error && <div style={{color: 'red', marginBottom: '10px'}}>{error}</div>}
      {Object.keys(formData).map((key) => (
        <input
          key={key}
          type={['invitedGuests', 'attendedGuests', 'totalCost', 'advancedAmount', 'pendingAmount'].includes(key) ? "number" : "text"}
          name={key}
          placeholder={key.replace(/([A-Z])/g, ' $1').replace(/^./, function(str){ return str.toUpperCase(); })}
          value={formData[key]}
          onChange={handleChange}
          required
          min={key.includes('Guests') || key.includes('Cost') || key.includes('Amount') ? 0 : undefined}
        />
      ))}
      <button type="submit">Submit</button>
    </form>
  );
};

export default ReportForm;
