import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const AddEvent = () => {
  const navigate = useNavigate();
  const [eventData, setEventData] = useState({
    eventType: "",
    eventName: "",
    contactNumber: "",
    email: "",
    date: "",
    guestCount: "",
    guestDetails: "",
    specialNotes: "",
  });

  const handleChange = (e) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      // Simulate API call to save event
      const savedEvent = await saveEventToDatabase(eventData);
      
      // Check if event was saved successfully and has an ID
      if (savedEvent && savedEvent._id) {
        // Navigate to event summary page with the event ID
        navigate(`/event-summary/${savedEvent._id}`);
      } else {
        throw new Error("Failed to save event");
      }
    } catch (error) {
      console.error("Error adding event:", error);
      alert("Failed to submit the event. Please try again.");
    }
  };

  // Simulated function to save event to database
  const saveEventToDatabase = async (eventData) => {
    // This would normally be an axios or fetch call to your backend
    return new Promise((resolve, reject) => {
      // Simulate server-side ID generation
      const eventId = generateUniqueId();
      
      // Simulate successful database save
      setTimeout(() => {
        resolve({
          ...eventData,
          _id: eventId
        });
      }, 500); // Simulate network delay
    });
  };

  // Utility function to generate a unique ID
  const generateUniqueId = () => {
    return 'event_' + Math.random().toString(36).substr(2, 9);
  };

  return (
    <div className="add-event">
      <h1>Add New Event</h1>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          name="eventType" 
          placeholder="Event Type" 
          onChange={handleChange} 
          required 
        />
        <input 
          type="text" 
          name="eventName" 
          placeholder="Event Name" 
          onChange={handleChange} 
          required 
        />
        <input 
          type="text" 
          name="contactNumber" 
          placeholder="Contact Number" 
          onChange={handleChange} 
          required 
        />
        <input 
          type="email" 
          name="email" 
          placeholder="Email" 
          onChange={handleChange} 
          required 
        />
        <input 
          type="date" 
          name="date" 
          onChange={handleChange} 
          required 
        />
        <input 
          type="number" 
          name="guestCount" 
          placeholder="Guest Count" 
          onChange={handleChange} 
          required 
        />
        <textarea 
          name="guestDetails" 
          placeholder="Guest Details" 
          onChange={handleChange}
        ></textarea>
        <textarea 
          name="specialNotes" 
          placeholder="Special Notes" 
          onChange={handleChange}
        ></textarea>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AddEvent;
