import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './TaskList.css';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tasks');
        
        // Ensure we're working with an array
        const tasksData = Array.isArray(response.data) 
          ? response.data 
          : response.data.tasks || response.data.Task || [];
          
        setTasks(tasksData);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
        setTasks([]); // Ensure tasks is always an array
      }
    };
    fetchTasks();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/tasks/${id}`);
      setTasks(tasks.filter(task => task._id !== id));
    } catch (err) {
      alert('Failed to delete task');
    }
  };

  if (loading) return <div className="loading">Loading tasks...</div>;
  if (error) return <div className="error">Error: {error}</div>;
  if (!Array.isArray(tasks)) return <div className="error">Invalid tasks data format</div>;
  if (tasks.length === 0) return <div className="no-tasks">No tasks found</div>;

  return (
    <div className="task-list-container">
      <h2>Task List</h2>
      <button className="create-btn" onClick={() => navigate('/create-task')}>
        Create New Task
      </button>
      
      <div className="task-grid">
        {tasks.map((task) => (
          <div key={task._id} className="task-card">
            <div className="task-header">
              <h3>{task.taskName}</h3>
              <span className={`status-badge ${task.status.replace(' ', '-')}`}>
                {task.status}
              </span>
            </div>
            
            <div className="task-details">
              <p><strong>ID:</strong> {task.taskID}</p>
              <p><strong>Date:</strong> {new Date(task.taskDate).toLocaleDateString()}</p>
              <p><strong>Process:</strong> {task.process}</p>
              <p><strong>Assigned:</strong> {task.person}</p>
              <p><strong>Description:</strong> {task.description}</p>
            </div>
            
            <div className="task-actions">
              <button 
                className="update-btn"
                onClick={() => navigate(`/update-task/${task._id}`)}
              >
                Update
              </button>
              <button 
                className="delete-btn"
                onClick={() => handleDelete(task._id)}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TaskList;
